import { useState, createContext } from "react";
import "./App.css";
import { Container } from "react-bootstrap";
import { useMsal, useMsalAuthentication } from "@azure/msal-react";
import { InteractionType } from "@azure/msal-browser";
import { AuthenticatedTemplate, UnauthenticatedTemplate } from "@azure/msal-react";

import Login from "./components/Login";
import TeacherDashBoard from "./components/TeacherDashBoard";
import StudentDashBoard from "./components/StudentDashBoard";
import Book from "./components/Book";
import Footer from "./components/Footer";
import Header from "./components/Header";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import EquipmentTable from "./components/EquipmentTable";
import BookingForStudent from "./components/BookingForTeacher";
export const UserContext = createContext();

function App() {
  useMsalAuthentication(InteractionType.Popup);
  const [user, setUser] = useState("");
  const [isStudent, setIsStudent] = useState(true);

  // async function getUserInfo() {
  //   const response = await fetch("/.auth/me");
  //   const payload = await response.json();
  //   const { clientPrincipal } = payload;
  //   return clientPrincipal;
  // }

  // (async () => {
  //   console.log(await getUserInfo());
  // })();

  function Render() {
    const { accounts } = useMsal();

    try {
      const username = accounts[0].username;
      setUser(username);
      setIsStudent(True);
    } catch (e) {}
  }
  console.log(user);
  console.log(isStudent);
  if (user !== "")
    return (
      <div className="App">
        <UserContext.Provider value={[user, isStudent]}>
          <UnauthenticatedTemplate>
            <Login />
          </UnauthenticatedTemplate>
          <AuthenticatedTemplate>
            <Header />
            <div className="flexs">
              {/* <hi className="mx-auto">Hello {user}</hi>  */}
              <Container>
                <Routes>
                  <Route path="/" exact element={<EquipmentTable />}></Route>
                  <Route path="/book" element={<Book />}></Route>
                  {isStudent ? <Route path="/student" element={<StudentDashBoard />}></Route> : <Route path="/teacher" element={<BookingForStudent />}></Route>}
                </Routes>
              </Container>
            </div>
            <Footer />
          </AuthenticatedTemplate>
        </UserContext.Provider>
      </div>
    );
  else
    return (
      <>
        {Render()}
        <Login />
      </>
    );
}

export default App;
